package integerVariables;

public class 정수리터럴 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 101; // 10진수
		int b = 0146; // 8진수
		int c = 0x1c2b; // 16진수
		int d = 0b1111; // 이진수
		long l = 12314445l;
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
	}

}
